//this component controlls the signup for any athlete or coach
//arguments: fullName, email, password, team, keys

import { useState } from 'react'
import { useQuery ,useMutation, gql } from '@apollo/client'
import styled from 'styled-components'

import { ALL_TEAMS_QUERY } from '../lib/querys'

import Button from '../components/styled/Button'

const SignupForm = styled.form`
    height: 90vh;
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
`

const Input = styled.input`
    width: 80%;
    font-size: 1.8rem;
    cursor: pointer;

    &:active {
        text-decoration: none;
    }
`

const Signup = () => {

    const { loading, data } = useQuery(ALL_TEAMS_QUERY)
    const [ fullName, setFullName ] = useState('')
    const [ email, setEmail ] = useState('')
    const [ password, setPassword ] = useState('')
    const [ confirmPassword, setConfirmPassword ] = useState('')
    const [ teamKey, setTeamKey ] = useState('')
    const [ coachKey, setCoachKey ] = useState('')

    return (
        <SignupForm>
            <p>Join your team</p>
            <Input 
                value={fullName}
                onChange={e => setFullName(e.target.value)}
                type='text'
                placeholder='Full Name'
                required
            />

            <Input
                value={email}
                onChange={e => setEmail(e.target.value)}
                type='email'
                placeholder='Email Address'
                required
            />

            <Input
                value={password}
                onChange={e => setPassword(e.target.value)}
                type='password'
                placeholder='Enter password'
                required
            />

            <Input
                value={confirmPassword}
                onChange={e => setConfirmPassword(e.target.value)}
                type='password'
                placeholder='Confirm password'
                required
            />

            <Input 
                value={teamKey}
                onChange={e => setTeamKey(e.target.value)}
                type='text'
                placeholder='Enter Team Key'
                required
            />
            <Button>Sign up!</Button>
        </SignupForm>
    )
}

export default Signup
